        ],
        feasibility: 'Medium'
      },
      {
        service: 'Hybrid Approach (City by City)',
        estimatedRestaurants: this.estimatedUSPizzaRestaurants,
        costPerRequest: 0, // Manual + free APIs
        totalCost: 0,
        pros: [
          'Can start with high-density cities',
          'Incremental cost and complexity',
          'Focus on sourdough-likely markets first',
          'Build database gradually'
        ],
        cons: [
          'Time-intensive',
          'Inconsistent coverage',
          'Manual work required'
        ],
        feasibility: 'High'
      }
    ];

    return analyses;
  }

  displayAnalysis(analyses: CostAnalysis[]): void {
    analyses.forEach((analysis, index) => {
      console.log(`\n${index + 1}. ${analysis.service}`);
      console.log(`   ðŸ“Š Coverage: ${analysis.estimatedRestaurants.toLocaleString()} restaurants`);
      console.log(`   ðŸ’° Total Cost: $${analysis.totalCost.toLocaleString()}`);
      console.log(`   ðŸŽ¯ Feasibility: ${analysis.feasibility}`);

      console.log(`   âœ… Pros:`);
      analysis.pros.forEach(pro => console.log(`      â€¢ ${pro}`));

      console.log(`   âŒ Cons:`);
      analysis.cons.forEach(con => console.log(`      â€¢ ${con}`));
