    console.log('3. ðŸ—ï¸  Build city-by-city discovery system');
    console.log('4. ðŸ“Š Measure sourdough adoption rates in stronghold cities');
    console.log('5. ðŸ“ˆ Use data to guide expansion strategy');

    console.log(`\nðŸ’¡ WHY THIS APPROACH WORKS:`);
    console.log('â€¢ Manageable costs (free to start)');
    console.log('â€¢ Focus on highest-value markets first');
    console.log('â€¢ Build proven system before scaling');
    console.log('â€¢ Generate revenue/interest from quality data in key markets');
    console.log('â€¢ Avoid $1,000+ upfront API costs');
  }
}

// Market size research
export class PizzaMarketResearch {

  analyzeMarketSize(): void {
    console.log('\nðŸ“ˆ US PIZZA MARKET SIZE ANALYSIS:');
    console.log('=' .repeat(40));

    const marketStats = {
      totalPizzaMarketValue: '50+ billion USD',
      pizzeriaCount: '70,000-80,000 estimated',
      chainVsIndependent: '60% chain, 40% independent',
      averageRevenuePerStore: '~$650,000 annually',
      topChains: ['Dominos (~19,000 locations)', 'Pizza Hut (~18,000)', 'Papa Johns (~5,500)'],
      independentEstimate: '28,000-32,000 independent pizzerias'
    };

    Object.entries(marketStats).forEach(([key, value]) => {
      const label = key.replace(/([A-Z])/g, ' $1').toLowerCase();
      if (Array.isArray(value)) {
        console.log(`${label}: ${value.join(', ')}`);
      } else {
        console.log(`${label}: ${value}`);
      }
    });

    console.log(`\nðŸŽ¯ SOURDOUGH OPPORTUNITY:`);
    console.log('â€¢ Independent pizzerias most likely to use sourdough');
    console.log('â€¢ 28,000-32,000 independent restaurants to analyze');
    console.log('â€¢ Chain restaurants rarely use sourdough (cost/complexity)');
    console.log('â€¢ Focus discovery on independents for best ROI');
  }
}

// Main execution
async function main() {
  const analysis = new NationwidePizzaDiscoveryAnalysis();
  const market = new PizzaMarketResearch();

  const costAnalyses = analysis.analyzeCostOptions();
  analysis.displayAnalysis(costAnalyses);

  market.analyzeMarketSize();

  analysis.recommendStrategy();
  analysis.suggestImmediateActions();
}

main().catch(console.error);

