#!/usr/bin/env tsx

import { db } from './db';
import { restaurants } from '../shared/schema';
import axios from 'axios';
import * as cheerio from 'cheerio';

interface OutscraperResult {
  name: string;
  address: string;
  phone: string;
  website: string;
  rating: number;
  reviews_count: number;
  latitude: number;
  longitude: number;
  description: string;
  category: string[];
}

class OutscraperIntegration {
  private apiKey: string;
  private sourdoughKeywords = ['sourdough', 'naturally leavened', 'wild yeast'];

  constructor() {
    this.apiKey = process.env.OUTSCRAPER_API_KEY || '';
    if (!this.apiKey) {
      console.log('âš ï¸  OUTSCRAPER_API_KEY not found in environment');
      console.log('ðŸ“‹ To get authentic restaurant data:');
      console.log('   1. Visit https://outscraper.com/');
      console.log('   2. Sign up for free account (100 requests included)');
      console.log('   3. Add OUTSCRAPER_API_KEY to environment');
    }
  }

  async searchPizzaRestaurants(city: string, state: string): Promise<OutscraperResult[]> {
    if (!this.apiKey) {
      console.log(`âŒ Cannot search ${city}, ${state} - API key required`);
      return [];
    }

    try {
      const query = `pizza restaurants in ${city}, ${state}`;
      console.log(`ðŸ” Searching: ${query}`);

      const response = await axios.get('https://api.outscraper.com/maps/search-v3', {
        params: {
          query,
          limit: 50,
          language: 'en',
          region: 'US'
        },
        headers: {
          'X-API-KEY': this.apiKey
        }
      });

      return response.data.data || [];

    } catch (error) {
      console.log(`âŒ Outscraper API error: ${error.message}`);
      return [];
    }
  }

  async verifyWebsiteForSourdough(name: string, website: string): Promise<{
    verified: boolean;
    keywords: string[];
    description: string;
  }> {
    if (!website) {
      return { verified: false, keywords: [], description: '' };
    }

    try {
      console.log(`    Verifying ${name} website: ${website}`);

      const response = await axios.get(website, {
        timeout: 10000,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        }
      });

      const $ = cheerio.load(response.data);
      const content = $('body').text().toLowerCase();

      // Check for sourdough keywords
      const foundKeywords = this.sourdoughKeywords.filter(keyword => 
        content.includes(keyword.toLowerCase())
      );

      // Get authentic description from meta tag or first paragraph
      let description = '';
      const metaDesc = $('meta[name="description"]').attr('content');
      if (metaDesc && metaDesc.length > 20) {
        description = metaDesc;
      } else {
        const firstPara = $('p').first().text().trim();
        if (firstPara && firstPara.length > 50) {
          description = firstPara.substring(0, 200) + '...';
        }
      }

      console.log(`    Found keywords: [${foundKeywords.join(', ')}]`);

      return {
        verified: foundKeywords.length > 0,
        keywords: foundKeywords,
        description: description || `${name} restaurant`
      };

    } catch (error) {
      console.log(`    Website verification failed: ${error.message}`);
      return { verified: false, keywords: [], description: '' };
    }
  }

  async discoverAndVerifyCity(city: string, state: string) {
    console.log(`\nðŸ™ï¸  DISCOVERING: ${city}, ${state}`);
    console.log('=' .repeat(50));

    if (!this.apiKey) {
      console.log('âŒ API key required for authentic data discovery');
      return { found: 0, verified: 0 };
    }

    // Get real restaurant data from Outscraper
    const restaurants = await this.searchPizzaRestaurants(city, state);
    console.log(`ðŸ“Š Found ${restaurants.length} pizza restaurants`);

    let verified = 0;

    for (const restaurant of restaurants) {
      if (!restaurant.website) {
        console.log(`â­ï¸  Skipping ${restaurant.name} (no website)`);
        continue;
      }

      // Verify sourdough claims on restaurant's official website
      const verification = await this.verifyWebsiteForSourdough(
        restaurant.name, 
        restaurant.website
      );

      if (verification.verified) {
        console.log(`âœ… VERIFIED: ${restaurant.name}`);
        console.log(`   Address: ${restaurant.address}`);
        console.log(`   Phone: ${restaurant.phone}`);
        console.log(`   Rating: ${restaurant.rating} (${restaurant.reviews_count} reviews)`);
        console.log(`   Keywords: [${verification.keywords.join(', ')}]`);

        // Add to database with ONLY authentic data
        await db.insert(restaurants).values({
          name: restaurant.name,
          address: restaurant.address,
          city: city,
          state: state,
          phone: restaurant.phone,
          website: restaurant.website,
          description: verification.description,
          sourdoughVerified: 1,
          sourdoughKeywords: verification.keywords,
          rating: restaurant.rating,
          reviewCount: restaurant.reviews_count,
          latitude: restaurant.latitude,
          longitude: restaurant.longitude,
          imageUrl: "https://images.unsplash.com/photo-1513104890138-7c749659a591?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=400"
        });

        verified++;
      } else {
        console.log(`âŒ No sourdough: ${restaurant.name}`);
      }

      // Be respectful to websites
      await new Promise(resolve => setTimeout(resolve, 2000));
    }

    return { found: restaurants.length, verified };
  }
}

export async function discoverAuthenticSourdough() {
  console.log('ðŸ” AUTHENTIC SOURDOUGH DISCOVERY SYSTEM');
  console.log('=' .repeat(55));
  console.log('âœ… Using Outscraper API for real business data');
  console.log('âœ… Verifying sourdough claims on official websites');
  console.log('ðŸš« No fabricated information will be added');

  const integration = new OutscraperIntegration();

  // Start with high-probability sourdough cities
  const priorityCities = [
    { city: 'San Francisco', state: 'CA' },
    { city: 'Berkeley', state: 'CA' },
    { city: 'Portland', state: 'OR' }
  ];

  let totalFound = 0;
  let totalVerified = 0;

  for (const location of priorityCities) {
    const results = await integration.discoverAndVerifyCity(location.city, location.state);
    totalFound += results.found;
    totalVerified += results.verified;
  }

  console.log(`\nðŸ“Š DISCOVERY COMPLETE:`);
  console.log(`   ðŸ” Total restaurants found: ${totalFound}`);
  console.log(`   âœ… Sourdough verified: ${totalVerified}`);
  console.log(`   ðŸ“ˆ Sourdough adoption rate: ${totalFound > 0 ? ((totalVerified / totalFound) * 100).toFixed(1) : 0}%`);

  return { totalFound, totalVerified };
}

if (import.meta.url.endsWith(process.argv[1])) {
  discoverAuthenticSourdough().catch(console.error);
}

