];

export async function buildRapidDirectory() {
  const apiKey = process.env.OUTSCRAPER_API_KEY;

  if (!apiKey) {
    console.log('âŒ OUTSCRAPER_API_KEY not found');
    return;
  }

  console.log('ðŸš€ RAPID DIRECTORY BUILDER - COMPREHENSIVE 50-CITY SCAN');
