    console.log(`\nðŸ“¦ Batch ${Math.floor(i/batchSize) + 1}/${Math.ceil(COMPREHENSIVE_CITIES.length/batchSize)}: ${batch.length} cities`);

    for (const cityState of batch) {
      const [city, state] = cityState.split(',');

      try {
