  console.log(`ðŸ—ºï¸  Directory ready for nationwide searches and map visualization`);

  return { totalVerified, processed, failed };
}

if (import.meta.url.endsWith(process.argv[1])) {
  runFullDiscovery().catch(console.error);
}

