#!/usr/bin/env tsx

import { db } from './db';
import { restaurants } from '../shared/schema';
import axios from 'axios';
import * as cheerio from 'cheerio';
import { eq } from 'drizzle-orm';

const SOURDOUGH_KEYWORDS = ['sourdough', 'naturally leavened', 'wild yeast', 'naturally fermented'];

class SFPizzaBaseline {
  private apiKey: string;
  private allPizzaPlaces: any[] = [];
  private verified = 0;
  private failed = 0;
  private processed = 0;

  constructor() {
    this.apiKey = process.env.OUTSCRAPER_API_KEY || '';
  }

  async discoverAllSFPizza() {
    console.log('ðŸ• SAN FRANCISCO PIZZA BASELINE DISCOVERY');
    console.log('=' .repeat(60));
    console.log('Step 1: Find ALL pizza establishments');
    console.log('Step 2: Apply sourdough verification to each');

    // Simple, direct searches to find maximum pizza establishments
    const searches = [
      'pizza San Francisco CA',
      'pizzeria San Francisco',  
      'italian restaurant San Francisco',
      'bakery San Francisco',
      'restaurant pizza San Francisco'
    ];

    for (const query of searches) {
      await this.executeDirectSearch(query);
      await new Promise(resolve => setTimeout(resolve, 3000));
    }

    // Show raw results for debugging
    console.log(`\nðŸ“Š RAW SEARCH RESULTS:`);
    console.log(`   Total establishments found: ${this.allPizzaPlaces.length}`);

    if (this.allPizzaPlaces.length > 0) {
      console.log(`\nðŸ” SAMPLE RESULTS (first 5):`);
      this.allPizzaPlaces.slice(0, 5).forEach((place, index) => {
        console.log(`   ${index + 1}. ${place.name || 'No name'}`);
        console.log(`      Categories: ${place.categories?.join(', ') || 'None'}`);
        console.log(`      Description: ${place.description?.substring(0, 100) || 'None'}...`);
      });
    }

    // Remove exact duplicates
    const uniquePlaces = this.removeDuplicates();
    console.log(`   Unique establishments: ${uniquePlaces.length}`);

    // Now verify each for sourdough
    console.log(`\nðŸ”¬ SOURDOUGH VERIFICATION PHASE:`);
    for (const place of uniquePlaces) {
      await this.verifySourdough(place);
    }

    return this.getStats();
  }

  async executeDirectSearch(query: string) {
    console.log(`\nðŸ” Searching: ${query}`);

    if (!this.apiKey) {
      console.log('   No API key - skipping');
      return;
    }

    try {
      const response = await axios.get('https://api.outscraper.com/maps/search-v3', {
        params: {
          query,
          limit: 20,
          language: 'en',
          region: 'US'
        },
        headers: {
          'X-API-KEY': this.apiKey
        }
      });

      if (response.data.status === 'Pending') {
        await new Promise(resolve => setTimeout(resolve, 8000));

        const resultResponse = await axios.get(`https://api.outscraper.com/requests/${response.data.id}`, {
          headers: {
            'X-API-KEY': this.apiKey
          }
        });

        if (resultResponse.data.status === 'Success' && resultResponse.data.data) {
          const results = resultResponse.data.data;
          console.log(`   Found ${results.length} establishments`);

          // Add ALL results, don't filter yet - let's see what we get
          this.allPizzaPlaces.push(...results);
        } else {
          console.log(`   No data in response`);
        }
      } else {
        console.log(`   Unexpected response status: ${response.data.status}`);
      }
    } catch (error) {
      console.log(`   Search error: ${error.message}`);
    }
  }

  removeDuplicates() {
    const seen = new Set();
    return this.allPizzaPlaces.filter(place => {
      if (!place.name) return false;

      const key = place.name.toLowerCase().trim();
      if (seen.has(key)) return false;

      seen.add(key);
      return true;
    });
  }

  async verifySourdough(place: any) {
    if (!place.name) return;

    this.processed++;
    console.log(`\n[${this.processed}] VERIFYING: ${place.name}`);

    // Debug: Show what we're working with
    console.log(`   Categories: ${place.categories?.join(', ') || 'None'}`);
    console.log(`   Description: ${place.description?.substring(0, 120) || 'None'}...`);

    try {
      // Check if already exists
      const existing = await db.select().from(restaurants).where(eq(restaurants.name, place.name));
      if (existing.length > 0) {
        console.log(`   Already in database`);
        return;
      }

      // Check if this is actually pizza-related
      if (!this.isPizzaRelevant(place)) {
        console.log(`   Not pizza-related - skipping`);
        return;
      }

      let websiteKeywords: string[] = [];
      let businessKeywords: string[] = [];

      // 1. Check website if available
      if (place.website) {
        console.log(`   Checking website: ${place.website}`);
        try {
          const response = await axios.get(place.website, {
            timeout: 12000,
            headers: {
              'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
            }
          });

          const $ = cheerio.load(response.data);
          const content = $('body').text().toLowerCase();

          websiteKeywords = SOURDOUGH_KEYWORDS.filter(keyword => 
            content.includes(keyword.toLowerCase())
          );

          if (websiteKeywords.length > 0) {
            console.log(`   ðŸŽ¯ Website keywords: [${websiteKeywords.join(', ')}]`);
          }
        } catch (error) {
          console.log(`   Website error: ${error.message}`);
        }
      }

      // 2. Check Google Business profile description
      if (place.description) {
        const businessContent = place.description.toLowerCase();
        businessKeywords = SOURDOUGH_KEYWORDS.filter(keyword => 
          businessContent.includes(keyword.toLowerCase())
        );

        if (businessKeywords.length > 0) {
          console.log(`   ðŸŽ¯ Business keywords: [${businessKeywords.join(', ')}]`);
        }
      }

      // 3. Combine results
      const allKeywords = [...new Set([...websiteKeywords, ...businessKeywords])];

      if (allKeywords.length === 0) {
        console.log(`   âŒ No sourdough keywords found`);
        this.failed++;
        return;
      }

      console.log(`   âœ… SOURDOUGH VERIFIED: [${allKeywords.join(', ')}]`);

      // Add to database
      let description = place.description || `${place.name} - verified sourdough pizza establishment in San Francisco`;
      if (description.length > 240) {
        description = description.substring(0, 240) + '...';
      }

      await db.insert(restaurants).values({
        name: place.name,
        address: place.address || '',
        city: "San Francisco",
        state: "CA",
        zipCode: place.postal_code || '',
        phone: place.phone || '',
        website: place.website || '',
        description,
        sourdoughVerified: 1,
        sourdoughKeywords: allKeywords,
        rating: place.rating || 0,
        reviewCount: place.reviews_count || 0,
        latitude: place.latitude || 0,
        longitude: place.longitude || 0,
        imageUrl: "https://images.unsplash.com/photo-1513104890138-7c749659a591?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=400"
      });

      this.verified++;
      console.log(`   ðŸ’¾ ADDED TO DATABASE - Total: ${this.verified}`);

    } catch (error) {
      console.log(`   Error: ${error.message}`);
      this.failed++;
    }
  }

  isPizzaRelevant(place: any): boolean {
    const name = (place.name || '').toLowerCase();
    const description = (place.description || '').toLowerCase();
    const categories = place.categories || [];

    // Check name
    if (name.includes('pizza') || name.includes('pizzeria')) {
      return true;
    }

    // Check description
    if (description.includes('pizza') || description.includes('pizzeria')) {
      return true;
    }

    // Check categories for pizza, italian, or bakery
    const hasRelevantCategory = categories.some((cat: string) => {
      const catLower = cat.toLowerCase();
      return catLower.includes('pizza') || 
             catLower.includes('italian') || 
             catLower.includes('bakery');
    });

    // Also include if it's a bakery or Italian restaurant that might serve pizza
    if (name.includes('bakery') || name.includes('italian') || 
        description.includes('bakery') || description.includes('italian') ||
        hasRelevantCategory) {
      return true;
    }

    return false;
  }

  getStats() {
    return {
      processed: this.processed,
      verified: this.verified,
      failed: this.failed,
      successRate: this.processed > 0 ? ((this.verified / this.processed) * 100).toFixed(1) : '0'
    };
  }
}

export async function runSFPizzaBaseline() {
  const baseline = new SFPizzaBaseline();

  const stats = await baseline.discoverAllSFPizza();

  console.log(`\nðŸŽ‰ SF PIZZA BASELINE COMPLETE:`);
  console.log(`   Establishments processed: ${stats.processed}`);
  console.log(`   Sourdough verified: ${stats.verified}`);
  console.log(`   Failed verification: ${stats.failed}`);
  console.log(`   Success rate: ${stats.successRate}%`);

  // Show current SF results
  const sfRestaurants = await db.select().from(restaurants).where(eq(restaurants.city, 'San Francisco'));
  console.log(`\nðŸŒ‰ SAN FRANCISCO SOURDOUGH ESTABLISHMENTS: ${sfRestaurants.length}`);

  sfRestaurants.forEach((restaurant, index) => {
    console.log(`\n${index + 1}. ${restaurant.name}`);
    console.log(`   ðŸ“ ${restaurant.address || 'Address TBD'}`);
    console.log(`   ðŸ” Keywords: [${restaurant.sourdoughKeywords?.join(', ') || 'sourdough'}]`);
    console.log(`   ðŸŒ ${restaurant.website || 'No website'}`);
  });

  return stats.verified;
}

if (import.meta.url.endsWith(process.argv[1])) {
  runSFPizzaBaseline().catch(console.error);
}

