#!/usr/bin/env tsx

import axios from 'axios';
import * as cheerio from 'cheerio';
import { db } from './db';
import { restaurants } from '@shared/schema';
import type { InsertRestaurant } from '@shared/schema';

interface BusinessResult {
  name: string;
  address: string;
  phone?: string;
  website?: string;
  description?: string;
  source: 'Google' | 'Yelp';
  businessType?: string;
}

class SimplifiedSourdoughSearch {
  private outscraper_api_key: string;
  private sourdoughKeywords = [
    'sourdough',
    'naturally leavened', 
    'wild yeast',
    'naturally fermented'
  ];

  constructor() {
    this.outscraper_api_key = process.env.OUTSCRAPER_API_KEY!;
    if (!this.outscraper_api_key) {
      throw new Error('OUTSCRAPER_API_KEY is required');
    }
  }

  private containsSourdoughKeywords(text: string): boolean {
    if (!text) return false;
    const lowerText = text.toLowerCase();
    return this.sourdoughKeywords.some(keyword => 
      lowerText.includes(keyword.toLowerCase())
    );
  }

  private isPizzaRestaurant(name: string, businessType?: string): boolean {
    const lowerName = name.toLowerCase();
    const lowerType = (businessType || '').toLowerCase();

    // Include if name or type contains pizza-related terms
    const pizzaTerms = ['pizza', 'pizzeria', 'pizzette', 'pie'];
    const hasPizzaTerm = pizzaTerms.some(term => 
      lowerName.includes(term) || lowerType.includes(term)
    );

    // Exclude bakeries, delis, and other non-pizza businesses
    const excludeTerms = ['bakery', 'deli', 'bread', 'market', 'grocery'];
    const hasExcludeTerm = excludeTerms.some(term => 
      lowerName.includes(term) || lowerType.includes(term)
    );

    return hasPizzaTerm && !hasExcludeTerm;
  }

  private async scrapeWebsiteForSourdough(url: string): Promise<{ 
    hasSourdough: boolean; 
    content?: string; 
  }> {
    try {
      console.log(`     ðŸŒ Checking website: ${url}`);

      let cleanUrl = url.trim();
      if (!cleanUrl.startsWith('http://') && !cleanUrl.startsWith('https://')) {
        cleanUrl = 'https://' + cleanUrl;
      }

      const response = await axios.get(cleanUrl, {
        timeout: 8000,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        }
      });

      const $ = cheerio.load(response.data);
      $('script, style, noscript').remove();

      const bodyText = $('body').text();
      const metaDescription = $('meta[name="description"]').attr('content') || '';
      const title = $('title').text() || '';

      const allContent = `${title} ${metaDescription} ${bodyText}`.toLowerCase();
      const hasSourdough = this.containsSourdoughKeywords(allContent);

      return {
        hasSourdough,
        content: hasSourdough ? allContent.substring(0, 300) : undefined
      };

    } catch (error: any) {
      console.log(`     âš ï¸  Website check failed: ${error.message}`);
      return { hasSourdough: false };
    }
  }

  private async searchGoogleForSourdoughPizza(city: string, state: string): Promise<BusinessResult[]> {
    try {
      console.log(`ðŸ” Searching Google for "sourdough pizza ${city} ${state}"...`);

      const response = await axios.get('https://api.outscraper.com/maps/search-v3', {
        params: {
          query: `sourdough pizza ${city} ${state}`,
          language: 'en',
          region: 'US',
          limit: 100,
          async: false
        },
        headers: {
          'X-API-KEY': this.outscraper_api_key
        },
        timeout: 60000
      });

      if (!response.data || !response.data.data || !Array.isArray(response.data.data)) {
        console.log('No Google results found');
        return [];
      }

      const results = response.data.data.flat();
      console.log(`ðŸ“ Found ${results.length} Google results`);

      // Filter to pizza restaurants only
      const pizzaResults: BusinessResult[] = results
        .filter(r => this.isPizzaRestaurant(r.name, r.type))
        .map(r => ({
          name: r.name,
          address: r.full_address,
          phone: r.phone,
          website: r.site,
          description: r.description,
          source: 'Google' as const,
          businessType: r.type
        }));

      console.log(`ðŸ• Filtered to ${pizzaResults.length} pizza restaurants`);
      return pizzaResults;

    } catch (error: any) {
      console.error('âŒ Google search failed:', error.message);
      return [];
    }
  }

  private async searchYelpForSourdoughPizza(city: string, state: string): Promise<BusinessResult[]> {
    // Note: We could add Yelp API search here if API key is available
    // For now, return empty array but structure is ready
    console.log(`ðŸ” Yelp search for sourdough pizza in ${city}, ${state} (would require Yelp API key)`);
    return [];
  }

  private async verifyAndSaveResults(results: BusinessResult[], city: string, state: string): Promise<number> {
    console.log(`\nðŸ” Verifying ${results.length} pizza restaurants...`);

    let verifiedCount = 0;

    for (let i = 0; i < results.length; i++) {
      const business = results[i];
      console.log(`\n[${i + 1}/${results.length}] ðŸ• ${business.name}`);
      console.log(`   ðŸ“ ${business.address}`);
      console.log(`   ðŸ“Š Source: ${business.source}`);

      let isVerified = false;
      let verificationSource = '';
      let verificationContent = '';

      // Step 1: Check Google Business description first
      if (business.description && this.containsSourdoughKeywords(business.description)) {
        isVerified = true;
        verificationSource = 'Google Business Profile';
        verificationContent = business.description;
        console.log(`   âœ… SOURDOUGH FOUND in business description!`);
      }

      // Step 2: Only check website if NOT found in business description
      else if (business.website) {
        const websiteResult = await this.scrapeWebsiteForSourdough(business.website);
        if (websiteResult.hasSourdough) {
          isVerified = true;
          verificationSource = 'Restaurant Website';
          verificationContent = websiteResult.content || '';
          console.log(`   âœ… SOURDOUGH FOUND on website!`);
        } else {
          console.log(`   âŒ No sourdough keywords found`);
        }
      } else {
        console.log(`   âŒ No business description or website to check`);
      }

      if (isVerified && business.address) {
        try {
          const insertData: InsertRestaurant = {
            name: business.name,
            address: business.address,
            phone: business.phone || null,
            website: business.website || null,
            latitude: 37.7749, // Default SF coordinates - could extract exact location later
            longitude: -122.4194,
            description: `Verified sourdough pizza from ${verificationSource}: ${verificationContent?.substring(0, 200)}...`,
            cuisine: 'Italian',
            priceRange: '$-$$',
            rating: null,
            city: city,
            state: state
          };

          await db.insert(restaurants).values(insertData);
          console.log(`   ðŸ’¾ SAVED to database`);
          verifiedCount++;

        } catch (error: any) {
          if (error.message?.includes('duplicate')) {
            console.log(`   âš ï¸  Already exists in database`);
          } else {
            console.error(`   âŒ Error saving: ${error.message}`);
          }
        }
      }

      // Rate limiting
      await new Promise(resolve => setTimeout(resolve, 2000));
    }

    return verifiedCount;
  }

  async executeSimplifiedSearch(city: string = 'San Francisco', state: string = 'CA'): Promise<number> {
    console.log(`\nðŸš€ SIMPLIFIED SOURDOUGH PIZZA SEARCH for ${city}, ${state}`);
    console.log('ðŸ“‹ Strategy: Direct search for "sourdough pizza" â†’ verify pizza restaurants only');

    try {
      // Step 1: Search Google for "sourdough pizza [city] [state]"
      const googleResults = await this.searchGoogleForSourdoughPizza(city, state);

      // Step 2: Search Yelp (placeholder for now)
      const yelpResults = await this.searchYelpForSourdoughPizza(city, state);

      // Step 3: Combine and deduplicate results
      const allResults = [...googleResults, ...yelpResults];
      const uniqueResults = allResults.filter((business, index, self) => {
        const key = `${business.name}-${business.address}`.toLowerCase().replace(/[^a-z0-9]/g, '');
        return index === self.findIndex(b => 
          `${b.name}-${b.address}`.toLowerCase().replace(/[^a-z0-9]/g, '') === key
        );
      });

      console.log(`\nðŸ“Š Combined results: ${allResults.length} total, ${uniqueResults.length} unique pizza restaurants`);

      if (uniqueResults.length === 0) {
        console.log('âŒ No pizza restaurants found');
        return 0;
      }

      // Step 4: Verify each restaurant for sourdough
      const verifiedCount = await this.verifyAndSaveResults(uniqueResults, city, state);

      // Step 5: Final summary
      console.log(`\nðŸ“Š SIMPLIFIED SEARCH COMPLETE`);
      console.log(`ðŸ• Pizza restaurants found: ${uniqueResults.length}`);
      console.log(`âœ… Verified sourdough restaurants: ${verifiedCount}`);
      console.log(`ðŸ“ˆ Success rate: ${((verifiedCount / uniqueResults.length) * 100).toFixed(1)}%`);

      return verifiedCount;

    } catch (error: any) {
      console.error('âŒ Simplified search failed:', error.message);
      throw error;
    }
  }
}

// Execute directly
const searcher = new SimplifiedSourdoughSearch();
searcher.executeSimplifiedSearch('Los Angeles', 'CA')
  .then((count) => {
    console.log(`\nâœ… Simplified search completed successfully!`);
    console.log(`ðŸ¥– Found and verified ${count} sourdough pizza restaurants`);
    process.exit(0);
  })
  .catch((error) => {
    console.error('\nâŒ Search failed:', error);
    process.exit(1);
  });

server/social-media-discovery.ts.content:
import axios from 'axios';
import * as cheerio from 'cheerio';

interface SocialMediaProfile {
  platform: 'instagram' | 'facebook';
  username: string;
  url: string;
  bio?: string;
  hasSourdoughKeywords: boolean;
  foundKeywords: string[];
}

interface RestaurantSocialMedia {
  name: string;
  address: string;
  socialProfiles: SocialMediaProfile[];
  totalSourdoughProfiles: number;
}

export class SocialMediaDiscovery {
  private sourdoughKeywords = ['sourdough', 'naturally leavened', 'wild yeast', 'naturally fermented'];

  /**
   * Discover social media profiles for a restaurant using multiple methods
   */
  async findRestaurantSocialMedia(restaurantName: string, address: string, website?: string): Promise<RestaurantSocialMedia> {
    console.log(`ðŸ” Discovering social media for: ${restaurantName}`);

    const profiles: SocialMediaProfile[] = [];

    // Method 1: Check restaurant website for social media links
    if (website) {
      const websiteProfiles = await this.extractSocialFromWebsite(website);
      profiles.push(...websiteProfiles);
    }

    // Method 2: Direct Instagram search patterns
    const instagramProfiles = await this.searchInstagramProfiles(restaurantName, address);
    profiles.push(...instagramProfiles);

    // Method 3: Direct Facebook search patterns
    const facebookProfiles = await this.searchFacebookProfiles(restaurantName, address);
    profiles.push(...facebookProfiles);

    // Method 4: Google search for social media mentions
    const googleFoundProfiles = await this.googleSearchSocialMedia(restaurantName, address);
    profiles.push(...googleFoundProfiles);

    // Remove duplicates
    const uniqueProfiles = this.removeDuplicateProfiles(profiles);

    // Check each profile for sourdough keywords
    for (const profile of uniqueProfiles) {
      await this.checkProfileForSourdough(profile);
    }

    const sourdoughProfilesCount = uniqueProfiles.filter(p => p.hasSourdoughKeywords).length;

    console.log(`ðŸ“± Found ${uniqueProfiles.length} social profiles, ${sourdoughProfilesCount} with sourdough keywords`);

    return {
      name: restaurantName,
      address,
      socialProfiles: uniqueProfiles,
      totalSourdoughProfiles: sourdoughProfilesCount
    };
  }

  /**
   * Extract social media links from restaurant website
   */
  private async extractSocialFromWebsite(websiteUrl: string): Promise<SocialMediaProfile[]> {
    try {
      const response = await axios.get(websiteUrl, {
        timeout: 8000,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        }
      });

      const $ = cheerio.load(response.data);
      const profiles: SocialMediaProfile[] = [];

      // Look for Instagram links
      $('a[href*="instagram.com"]').each((i, link) => {
        const href = $(link).attr('href');
        if (href) {
          const username = this.extractInstagramUsername(href);
          if (username) {
            profiles.push({
              platform: 'instagram',
              username,
              url: `https://instagram.com/${username}`,
              hasSourdoughKeywords: false,
              foundKeywords: []
            });
          }
        }
      });

      // Look for Facebook links
      $('a[href*="facebook.com"]').each((i, link) => {
        const href = $(link).attr('href');
        if (href) {
          const username = this.extractFacebookUsername(href);
          if (username) {
            profiles.push({
              platform: 'facebook',
              username,
              url: `https://facebook.com/${username}`,
              hasSourdoughKeywords: false,
              foundKeywords: []
            });
          }
        }
      });

      return profiles;
    } catch (error) {
      console.log(`   âŒ Website social extraction failed: ${error.message}`);
      return [];
    }
  }

  /**
   * Search for Instagram profiles using common naming patterns
   */
  private async searchInstagramProfiles(restaurantName: string, address: string): Promise<SocialMediaProfile[]> {
    const profiles: SocialMediaProfile[] = [];

    // Generate potential Instagram usernames
    const potentialUsernames = this.generateInstagramUsernames(restaurantName);

    for (const username of potentialUsernames) {
      try {
        // We'll use a web search to see if this Instagram profile exists and get basic info
        const exists = await this.checkInstagramProfileExists(username);
        if (exists) {
          profiles.push({
            platform: 'instagram',
            username,
            url: `https://instagram.com/${username}`,
            hasSourdoughKeywords: false,
            foundKeywords: []
          });
        }
      } catch (error) {
        // Continue with next username
        continue;
      }
    }

    return profiles;
  }

  /**
   * Search for Facebook profiles using common naming patterns
   */
  private async searchFacebookProfiles(restaurantName: string, address: string): Promise<SocialMediaProfile[]> {
    const profiles: SocialMediaProfile[] = [];

    // Generate potential Facebook page names
    const potentialPages = this.generateFacebookPages(restaurantName);

    for (const pageName of potentialPages) {
      try {
        const exists = await this.checkFacebookPageExists(pageName);
        if (exists) {
          profiles.push({
            platform: 'facebook',
            username: pageName,
            url: `https://facebook.com/${pageName}`,
            hasSourdoughKeywords: false,
            foundKeywords: []
          });
        }
      } catch (error) {
        continue;
      }
    }

    return profiles;
  }

  /**
   * Use Google search to find social media mentions
   */
  private async googleSearchSocialMedia(restaurantName: string, address: string): Promise<SocialMediaProfile[]> {
    const profiles: SocialMediaProfile[] = [];

    try {
      // Use Outscraper to search for social media mentions
      const outscraper_api_key = process.env.OUTSCRAPER_API_KEY;
      if (!outscraper_api_key) return profiles;

      const searchQuery = `"${restaurantName}" instagram OR facebook site:instagram.com OR site:facebook.com`;

      const response = await axios.get('https://api.outscraper.com/google-search-v3', {
        params: {
          query: searchQuery,
          limit: 10,
          async: false
        },
        headers: {
          'X-API-KEY': outscraper_api_key
        },
        timeout: 15000
      });

      if (response.data?.data?.[0]) {
        const results = response.data.data[0];

        results.forEach(result => {
          if (result.link) {
            if (result.link.includes('instagram.com')) {
              const username = this.extractInstagramUsername(result.link);
              if (username) {
                profiles.push({
                  platform: 'instagram',
                  username,
                  url: result.link,
                  hasSourdoughKeywords: false,
                  foundKeywords: []
                });
              }
            } else if (result.link.includes('facebook.com')) {
              const username = this.extractFacebookUsername(result.link);
              if (username) {
                profiles.push({
                  platform: 'facebook',
                  username,
                  url: result.link,
                  hasSourdoughKeywords: false,
                  foundKeywords: []
                });
              }
            }
          }
        });
      }
    } catch (error) {
      console.log(`   âŒ Google social search failed: ${error.message}`);
    }

    return profiles;
  }

  /**
   * Generate potential Instagram usernames from restaurant name
   */
  private generateInstagramUsernames(restaurantName: string): string[] {
    const clean = restaurantName
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, '')  // Remove special chars
      .replace(/\s+/g, '');         // Remove spaces

    const withSpaces = restaurantName
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, '')
      .replace(/\s+/g, '_');        // Replace spaces with underscores

    const withDots = restaurantName
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, '')
      .replace(/\s+/g, '.');        // Replace spaces with dots

    // Common restaurant patterns
    const patterns = [
      clean,
      withSpaces,
      withDots,
      `${clean}pizza`,
      `${clean}pdx`,
      `${clean}portland`,
      `pizza${clean}`,
      `${clean}restaurant`,
      `${clean}_pizza`,
      `${clean}.pizza`,
      clean.replace('pizza', '').replace('pie', '').trim()
    ];

    return [...new Set(patterns)].filter(p => p.length > 2);
  }

  /**
   * Generate potential Facebook page names from restaurant name
   */
  private generateFacebookPages(restaurantName: string): string[] {
    const clean = restaurantName
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, '')
      .replace(/\s+/g, '-');

    const withDots = restaurantName
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, '')
      .replace(/\s+/g, '.');

    const patterns = [
      clean,
      withDots,
      restaurantName.replace(/\s+/g, ''),
      `${clean}-pizza`,
      `${clean}-portland`,
      clean.replace('pizza', '').replace('pie', '').trim()
    ];

    return [...new Set(patterns)].filter(p => p.length > 2);
  }

  /**
   * Check if Instagram profile exists (simplified check)
   */
  private async checkInstagramProfileExists(username: string): Promise<boolean> {
    try {
      // Use a web search to check if this Instagram profile has any mentions
      const outscraper_api_key = process.env.OUTSCRAPER_API_KEY;
      if (!outscraper_api_key) return false;

      const response = await axios.get('https://api.outscraper.com/google-search-v3', {
        params: {
          query: `site:instagram.com/${username}`,
          limit: 1,
          async: false
        },
        headers: {
          'X-API-KEY': outscraper_api_key
        },
        timeout: 8000
      });

      return response.data?.data?.[0]?.length > 0;
    } catch {
      return false;
    }
  }

  /**
   * Check if Facebook page exists (simplified check)
   */
  private async checkFacebookPageExists(pageName: string): Promise<boolean> {
    try {
      const outscraper_api_key = process.env.OUTSCRAPER_API_KEY;
      if (!outscraper_api_key) return false;

      const response = await axios.get('https://api.outscraper.com/google-search-v3', {
        params: {
          query: `site:facebook.com/${pageName}`,
          limit: 1,
          async: false
        },
        headers: {
          'X-API-KEY': outscraper_api_key
        },
        timeout: 8000
      });

      return response.data?.data?.[0]?.length > 0;
    } catch {
      return false;
    }
  }

  /**
   * Check social media profile for sourdough keywords
   */
  private async checkProfileForSourdough(profile: SocialMediaProfile): Promise<void> {
    try {
      let bio = '';

      if (profile.platform === 'instagram') {
        bio = await this.getInstagramBio(profile.username);
      } else if (profile.platform === 'facebook') {
        bio = await this.getFacebookDescription(profile.username);
      }

      profile.bio = bio;

      // Check for sourdough keywords in bio
      const lowerBio = bio.toLowerCase();
      const foundKeywords: string[] = [];

      this.sourdoughKeywords.forEach(keyword => {
        if (lowerBio.includes(keyword.toLowerCase())) {
          foundKeywords.push(keyword);
        }
      });

      profile.foundKeywords = foundKeywords;
      profile.hasSourdoughKeywords = foundKeywords.length > 0;

      if (profile.hasSourdoughKeywords) {
        console.log(`   âœ… ${profile.platform} @${profile.username} has sourdough keywords: ${foundKeywords.join(', ')}`);
      }

    } catch (error) {
      console.log(`   âŒ Failed to check ${profile.platform} @${profile.username}: ${error.message}`);
    }
  }

  /**
   * Get Instagram bio using web scraping approach
   */
  private async getInstagramBio(username: string): Promise<string> {
    try {
      // Use Google search to find Instagram profile information
      const outscraper_api_key = process.env.OUTSCRAPER_API_KEY;
      if (!outscraper_api_key) return '';

      const response = await axios.get('https://api.outscraper.com/google-search-v3', {
        params: {
          query: `site:instagram.com/${username}`,
          limit: 1,
          async: false
        },
        headers: {
          'X-API-KEY': outscraper_api_key
        },
        timeout: 8000
      });

      if (response.data?.data?.[0]?.[0]) {
        const result = response.data.data[0][0];
        return result.description || result.snippet || '';
      }

      return '';
    } catch {
      return '';
    }
  }

  /**
   * Get Facebook page description using web scraping approach
   */
  private async getFacebookDescription(pageName: string): Promise<string> {
    try {
      const outscraper_api_key = process.env.OUTSCRAPER_API_KEY;
      if (!outscraper_api_key) return '';

      const response = await axios.get('https://api.outscraper.com/google-search-v3', {
        params: {
          query: `site:facebook.com/${pageName}`,
          limit: 1,
          async: false
        },
        headers: {
          'X-API-KEY': outscraper_api_key
        },
        timeout: 8000
      });

      if (response.data?.data?.[0]?.[0]) {
        const result = response.data.data[0][0];
        return result.description || result.snippet || '';
      }

      return '';
    } catch {
      return '';
    }
  }

  /**
   * Extract Instagram username from URL
   */
  private extractInstagramUsername(url: string): string | null {
    const match = url.match(/instagram\.com\/([^\/\?]+)/);
    return match ? match[1] : null;
  }

  /**
   * Extract Facebook username/page from URL
   */
  private extractFacebookUsername(url: string): string | null {
    const match = url.match(/facebook\.com\/([^\/\?]+)/);
    return match ? match[1] : null;
  }

  /**
   * Remove duplicate profiles
   */
  private removeDuplicateProfiles(profiles: SocialMediaProfile[]): SocialMediaProfile[] {
    const seen = new Set();
    return profiles.filter(profile => {
      const key = `${profile.platform}:${profile.username}`;
      if (seen.has(key)) return false;
      seen.add(key);
      return true;
    });
  }

  /**
   * Test the social media discovery system with Pizza Creature
   */
  async testPizzaCreature(): Promise<void> {
    console.log('ðŸ§ª TESTING SOCIAL MEDIA DISCOVERY: Pizza Creature');
    console.log('');

    const result = await this.findRestaurantSocialMedia(
      'Pizza Creature',
      '7316 N Lombard St, Portland, OR 97203',
      'http://www.pizzacreature.com/'
    );

    console.log('ðŸ“Š RESULTS:');
    console.log(`   Restaurant: ${result.name}`);
    console.log(`   Total social profiles found: ${result.socialProfiles.length}`);
    console.log(`   Profiles with sourdough keywords: ${result.totalSourdoughProfiles}`);
    console.log('');

    result.socialProfiles.forEach(profile => {
      console.log(`ðŸ“± ${profile.platform.toUpperCase()}: @${profile.username}`);
      console.log(`   ðŸ”— ${profile.url}`);
      console.log(`   ðŸ“ Bio: ${profile.bio || 'Not available'}`);
      if (profile.hasSourdoughKeywords) {
        console.log(`   âœ… SOURDOUGH KEYWORDS: ${profile.foundKeywords.join(', ')}`);
      } else {
        console.log(`   âŒ No sourdough keywords found`);
      }
      console.log('');
    });
  }
}

