    return allRestaurants.filter(restaurant => {
      const lat = restaurant.latitude;
      const lng = restaurant.longitude;

      if (!lat || !lng) return false;

      return lat >= bounds.south && 
             lat <= bounds.north && 
             lng >= bounds.west && 
             lng <= bounds.east;
    });
  }
}

export const storage = new DatabaseStorage();

