#!/usr/bin/env tsx

import axios from 'axios';
import * as cheerio from 'cheerio';
import { db } from './db';
import { restaurants } from '@shared/schema';
import type { InsertRestaurant } from '@shared/schema';

interface BusinessResult {
  name: string;
  address: string;
  phone?: string;
  website?: string;
  description?: string;
  rating?: number;
  source: 'Google' | 'Yelp';
  businessType?: string;
  latitude?: number;
  longitude?: number;
}

class YelpEnhancedSearch {
  private outscraper_api_key: string;
  private yelp_api_key: string;
  private sourdoughKeywords = [
    'sourdough',
    'naturally leavened', 
    'wild yeast',
    'naturally fermented'
  ];

  constructor() {
    this.outscraper_api_key = process.env.OUTSCRAPER_API_KEY!;
    this.yelp_api_key = process.env.YELP_API_KEY!;

    if (!this.outscraper_api_key) {
      throw new Error('OUTSCRAPER_API_KEY is required');
    }
    if (!this.yelp_api_key) {
      console.log('âš ï¸  YELP_API_KEY not found - Yelp search will be skipped');
    }
  }

  private containsSourdoughKeywords(text: string): boolean {
    if (!text) return false;
    const lowerText = text.toLowerCase();
    return this.sourdoughKeywords.some(keyword => 
      lowerText.includes(keyword.toLowerCase())
    );
  }

  private isPizzaRestaurant(name: string, businessType?: string, categories?: string[]): boolean {
    const lowerName = name.toLowerCase();
    const lowerType = (businessType || '').toLowerCase();
    const allCategories = (categories || []).join(' ').toLowerCase();

    const pizzaTerms = ['pizza', 'pizzeria', 'pizzette', 'pie'];
    const hasPizzaTerm = pizzaTerms.some(term => 
      lowerName.includes(term) || lowerType.includes(term) || allCategories.includes(term)
    );

    const excludeTerms = ['bakery', 'deli', 'bread', 'market', 'grocery'];
    const hasExcludeTerm = excludeTerms.some(term => 
      lowerName.includes(term) || lowerType.includes(term) || allCategories.includes(term)
    );

    return hasPizzaTerm && !hasExcludeTerm;
  }

  private async scrapeWebsiteForSourdough(url: string): Promise<{ 
    hasSourdough: boolean; 
    content?: string; 
  }> {
    try {
      console.log(`     ðŸŒ Checking website: ${url}`);

      let cleanUrl = url.trim();
      if (!cleanUrl.startsWith('http://') && !cleanUrl.startsWith('https://')) {
        cleanUrl = 'https://' + cleanUrl;
      }

      const response = await axios.get(cleanUrl, {
        timeout: 8000,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'
        }
      });

      const $ = cheerio.load(response.data);
      $('script, style, noscript').remove();

      const bodyText = $('body').text();
      const metaDescription = $('meta[name="description"]').attr('content') || '';
      const title = $('title').text() || '';

      const allContent = `${title} ${metaDescription} ${bodyText}`.toLowerCase();
      const hasSourdough = this.containsSourdoughKeywords(allContent);

      return {
        hasSourdough,
        content: hasSourdough ? allContent.substring(0, 300) : undefined
      };

    } catch (error: any) {
      console.log(`     âš ï¸  Website check failed: ${error.message}`);
      return { hasSourdough: false };
    }
  }

  private async checkAlternativeSourcesForSourdough(businessName: string, address: string): Promise<{
    hasSourdough: boolean;
    source?: string;
    content?: string;
  }> {
    try {
      // Strategy 1: Search for restaurant + sourdough mentions online
      const sourdoughSearchQuery = `"${businessName}" sourdough pizza ${address.split(',')[0]}`;

      console.log(`     ðŸ” Searching: "${sourdoughSearchQuery}"`);

      const response = await axios.get('https://api.outscraper.com/maps/search-v3', {
        params: {
          query: sourdoughSearchQuery,
          language: 'en',
          region: 'US',
          limit: 10,
          async: false
        },
        headers: {
          'X-API-KEY': this.outscraper_api_key
        },
        timeout: 30000
      });

      if (response.data?.data?.[0]) {
        const results = response.data.data[0];

        for (const result of results) {
          // Check if this result mentions both the restaurant and sourdough
          const title = result.title || '';
          const snippet = result.snippet || '';
          const description = result.description || '';

          const allText = `${title} ${snippet} ${description}`.toLowerCase();

          // Verify this is about our restaurant
          const restaurantNameWords = businessName.toLowerCase().split(' ');
          const hasRestaurantName = restaurantNameWords.some(word => 
            word.length > 3 && allText.includes(word)
          );

          if (hasRestaurantName && this.containsSourdoughKeywords(allText)) {
            return {
              hasSourdough: true,
              source: 'Web Search Results',
              content: `${title}: ${snippet}`.substring(0, 200)
            };
          }
        }
      }

      return { hasSourdough: false };

    } catch (error: any) {
      console.log(`     âš ï¸  Alternative search failed: ${error.message}`);
      return { hasSourdough: false };
    }
  }

  private async scrapeSocialMediaForSourdough(url: string, platform: string): Promise<{
    hasSourdough: boolean;
    content?: string;
  }> {
    try {
      console.log(`     ðŸ“± Checking ${platform}: ${url}`);

      const response = await axios.get(url, {
        timeout: 8000,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36'
        }
      });

      const $ = cheerio.load(response.data);
      $('script, style, noscript').remove();

      // Extract text content from the page
      const bodyText = $('body').text();
      const metaDescription = $('meta[name="description"]').attr('content') || '';
      const metaProperty = $('meta[property="og:description"]').attr('content') || '';
      const title = $('title').text() || '';

      const allContent = `${title} ${metaDescription} ${metaProperty} ${bodyText}`.toLowerCase();
      const hasSourdough = this.containsSourdoughKeywords(allContent);

      return {
        hasSourdough,
        content: hasSourdough ? allContent.substring(0, 300) : undefined
      };

    } catch (error: any) {
      console.log(`     âš ï¸  ${platform} check failed: ${error.message}`);
      return { hasSourdough: false };
    }
  }

  private async searchGoogleForPizza(city: string, state: string): Promise<BusinessResult[]> {
    const searchTerms = [
      `artisan pizza ${city} ${state}`,
      `sourdough pizza ${city} ${state}`
    ];

    const allResults: BusinessResult[] = [];

    for (const searchTerm of searchTerms) {
      try {
        console.log(`ðŸ” Google: Searching "${searchTerm}"...`);

        const response = await axios.get('https://api.outscraper.com/maps/search-v3', {
          params: {
            query: searchTerm,
            language: 'en',
            region: 'US',
            limit: 100,
            async: false
          },
          headers: {
            'X-API-KEY': this.outscraper_api_key
          },
          timeout: 60000
        });

        if (!response.data || !response.data.data || !Array.isArray(response.data.data)) {
          console.log(`   No Google results found for "${searchTerm}"`);
          continue;
        }

        const results = response.data.data[0] || [];
        const pizzaResults = results.filter((business: any) => {
          return this.isPizzaRestaurant(business.name, business.type);
        }).map((business: any) => ({
          name: business.name,
          address: business.full_address || business.address || '',
          phone: business.phone || '',
          website: business.website || business.site || '',
          description: business.description || '',
          rating: business.rating || 0,
          source: 'Google' as const,
          businessType: business.type || '',
          latitude: business.latitude,
          longitude: business.longitude
        }));

        allResults.push(...pizzaResults);
        console.log(`   ðŸ“ Found ${results.length} results for "${searchTerm}"`);
        console.log(`   ðŸ• Filtered to ${pizzaResults.length} pizza restaurants`);

        // Rate limiting between searches
        await new Promise(resolve => setTimeout(resolve, 2000));

      } catch (error: any) {
        console.error(`   âŒ Google search failed for "${searchTerm}": ${error.message}`);
      }
    }

    // Remove duplicates based on name and address
    const uniqueResults = allResults.filter((restaurant, index, self) => 
      index === self.findIndex(r => 
        r.name === restaurant.name && r.address === restaurant.address
      )
    );

    console.log(`   ðŸ“Š Total Google results: ${allResults.length}`);
    console.log(`   ðŸ• Unique pizza restaurants: ${uniqueResults.length}`);
    return uniqueResults;
  }

  private async searchYelpForPizza(city: string, state: string): Promise<BusinessResult[]> {
    if (!this.yelp_api_key) {
      console.log('ðŸ” Yelp: Skipping (no API key provided)');
      return [];
    }

    try {
      console.log(`ðŸ” Yelp: Searching artisan and sourdough pizza in ${city}, ${state}...`);

      // Search for both artisan and sourdough pizza
      const searchTerms = ['artisan pizza', 'gourmet pizza', 'wood fired pizza', 'neapolitan pizza', 'sourdough pizza'];
      let allYelpResults: any[] = [];

      for (const term of searchTerms) {
        try {
          const response = await axios.get('https://api.yelp.com/v3/businesses/search', {
            headers: {
              'Authorization': `Bearer ${this.yelp_api_key}`,
              'Accept': 'application/json'
            },
            params: {
              term: term,
              location: `${city}, ${state}`,
              categories: 'pizza',
              limit: 50,
              sort_by: 'rating'
            },
            timeout: 8000
          });

          if (response.data && response.data.businesses) {
            allYelpResults.push(...response.data.businesses);
            console.log(`   ðŸ“ Found ${response.data.businesses.length} results for "${term}"`);
          }
        } catch (error: any) {
          console.log(`   âš ï¸  Yelp search failed for "${term}": ${error.message}`);
        }

        // Rate limiting
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      // Remove duplicates and filter
      const uniqueResults = allYelpResults.filter((business, index, self) => 
        index === self.findIndex(b => b.id === business.id)
      );

      const pizzaResults: BusinessResult[] = uniqueResults
        .filter(business => {
          const categories = business.categories?.map((c: any) => c.title) || [];
          return this.isPizzaRestaurant(business.name, '', categories);
        })
        .map(business => ({
          name: business.name,
          address: business.location?.display_address?.join(', ') || '',
          phone: business.phone,
          website: business.attributes?.menu_url || business.url, // Extract actual website from menu_url or fallback to Yelp URL
          description: '', // Yelp doesn't provide description in search
          rating: business.rating,
          source: 'Yelp' as const,
          businessType: business.categories?.map((c: any) => c.title).join(', '),
          latitude: business.coordinates?.latitude,
          longitude: business.coordinates?.longitude
        }));

      console.log(`   ðŸ• Total unique Yelp pizza restaurants: ${pizzaResults.length}`);
      return pizzaResults;

    } catch (error: any) {
      console.error('   âŒ Yelp search failed:', error.message);
      return [];
    }
  }

  private async verifyAndSaveResults(results: BusinessResult[], city: string, state: string): Promise<number> {
    console.log(`\nðŸ” Verifying ${results.length} pizza restaurants...`);
    console.log(`ðŸ“‹ Enhanced multi-source verification:`);
    console.log(`   1ï¸âƒ£ Google Business profiles for sourdough keywords`);
    console.log(`   2ï¸âƒ£ Restaurant websites for sourdough content`);
    console.log(`   3ï¸âƒ£ Web search for restaurant + sourdough mentions`);
    console.log(`   4ï¸âƒ£ Alternative online sources verification`);
    console.log(`â±ï¸  Estimated time: ${Math.ceil(results.length * 2.5 / 60)} minutes\n`);

    let verifiedCount = 0;

    for (let i = 0; i < results.length; i++) {
      const business = results[i];
      console.log(`\n[${i + 1}/${results.length}] ðŸ• ${business.name}`);
      console.log(`   ðŸ“ ${business.address}`);
      console.log(`   ðŸ“Š Source: ${business.source}`);

      let isVerified = false;
      let verificationSource = '';
      let verificationContent = '';

      // Step 1: Check Google Business description first
      if (business.description && this.containsSourdoughKeywords(business.description)) {
        isVerified = true;
        verificationSource = 'Google Business Profile';
        verificationContent = business.description;
        console.log(`   âœ… SOURDOUGH FOUND in business description!`);
      }

      // Step 2: For Yelp results, check if business name contains sourdough keywords
      else if (business.source === 'Yelp' && this.containsSourdoughKeywords(business.name)) {
        isVerified = true;
        verificationSource = 'Yelp Business Name';
        verificationContent = business.name;
        console.log(`   âœ… SOURDOUGH FOUND in Yelp business name!`);
      }

      // Step 3: Check website if NOT found in business description/name
      else if (business.website && !business.website.includes('yelp.com')) {
        const websiteResult = await this.scrapeWebsiteForSourdough(business.website);
        if (websiteResult.hasSourdough) {
          isVerified = true;
          verificationSource = 'Restaurant Website';
          verificationContent = websiteResult.content || '';
          console.log(`   âœ… SOURDOUGH FOUND on website!`);
        }
      }

      // Step 4: Check alternative web sources if still not found
      if (!isVerified) {
        console.log(`   ðŸ” Checking alternative sources for sourdough mentions...`);
        const alternativeResult = await this.checkAlternativeSourcesForSourdough(business.name, business.address);

        if (alternativeResult.hasSourdough) {
          isVerified = true;
          verificationSource = alternativeResult.source || 'Alternative Web Sources';
          verificationContent = alternativeResult.content || '';
          console.log(`   âœ… SOURDOUGH FOUND in web search results!`);
        } else {
          console.log(`   âŒ No sourdough keywords found in any sources`);
        }
      }

      if (isVerified && business.address) {
        try {
          const insertData: InsertRestaurant = {
            name: business.name,
            address: business.address,
            phone: business.phone || null,
            website: business.website || null,
            latitude: business.latitude || 37.7749, // Default coordinates if not provided
            longitude: business.longitude || -122.4194,
            description: `Verified sourdough pizza from ${verificationSource}: ${verificationContent?.substring(0, 200)}...`,
            cuisine: 'Italian',
            priceRange: '$-$$',
            rating: business.rating || null,
            city: city,
            state: state
          };

          await db.insert(restaurants).values(insertData);
          console.log(`   ðŸ’¾ SAVED to database`);
          verifiedCount++;

        } catch (error: any) {
          if (error.message?.includes('duplicate')) {
            console.log(`   âš ï¸  Already exists in database`);
          } else {
            console.error(`   âŒ Error saving: ${error.message}`);
          }
        }
      }

      // Reduced rate limiting for faster processing
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Progress checkpoint every 25 restaurants
      if ((i + 1) % 25 === 0) {
        console.log(`\nðŸ“Š CHECKPOINT: Processed ${i + 1}/${results.length} restaurants`);
        console.log(`âœ… Found ${verifiedCount} sourdough establishments so far`);
        console.log(`ðŸ“ˆ Current success rate: ${((verifiedCount / (i + 1)) * 100).toFixed(1)}%`);
      }
    }

    return verifiedCount;
  }

  async executeArtisanSearch(city: string = 'San Francisco', state: string = 'CA'): Promise<number> {
    console.log(`\nðŸš€ ENHANCED PIZZA SEARCH for ${city}, ${state}`);
    console.log('ðŸ“‹ Strategy: Search "artisan pizza" + "sourdough pizza" â†’ verify for sourdough keywords');

    try {
      // Step 1: Search Google for artisan and sourdough pizza
      const googleResults = await this.searchGoogleForPizza(city, state);

      // Step 2: Search Yelp for artisan and sourdough pizza
      const yelpResults = await this.searchYelpForPizza(city, state);

      // Step 3: Combine and deduplicate results
      const allResults = [...googleResults, ...yelpResults];
      const uniqueResults = allResults.filter((business, index, self) => {
        const key = `${business.name}-${business.address}`.toLowerCase().replace(/[^a-z0-9]/g, '');
        return index === self.findIndex(b => 
          `${b.name}-${b.address}`.toLowerCase().replace(/[^a-z0-9]/g, '') === key
        );
      });

      console.log(`\nðŸ“Š Combined results:`);
      console.log(`ðŸ” Google results: ${googleResults.length}`);
      console.log(`ðŸ½ï¸  Yelp results: ${yelpResults.length}`);
      console.log(`ðŸ“ Total combined: ${allResults.length}`);
      console.log(`ðŸ• Unique pizza restaurants: ${uniqueResults.length}`);

      if (uniqueResults.length === 0) {
        console.log('âŒ No pizza restaurants found');
        return 0;
      }

      // Step 4: Verify each restaurant for sourdough
      const verifiedCount = await this.verifyAndSaveResults(uniqueResults, city, state);

      // Step 5: Final summary
      console.log(`\nðŸ“Š ARTISAN SEARCH COMPLETE`);
      console.log(`ðŸ” Artisan pizza restaurants found: ${uniqueResults.length}`);
      console.log(`âœ… Verified sourdough restaurants: ${verifiedCount}`);
      console.log(`ðŸ“ˆ Success rate: ${((verifiedCount / uniqueResults.length) * 100).toFixed(1)}%`);

      return verifiedCount;

    } catch (error: any) {
      console.error('âŒ Artisan search failed:', error.message);
      throw error;
    }
  }
}

// Execute directly
const searcher = new YelpEnhancedSearch();
searcher.executeArtisanSearch('Portland', 'OR')
  .then((count) => {
    console.log(`\nâœ… Yelp-enhanced search completed successfully!`);
    console.log(`ðŸ¥– Found and verified ${count} sourdough pizza restaurants`);
    process.exit(0);
  })
  .catch((error) => {
    console.error('\nâŒ Search failed:', error);
    process.exit(1);
  });

